const ethers = require("ethers");

const from = "3Dc7C8afc6AA6ac825D254b1d772817d230a233e";
const bytecode =
    "";
const initCodeHash = ethers.utils.keccak256(bytecode);
let salt = ethers.BigNumber.from(0);

const limit = ethers.BigNumber.from(2).pow(256);
while (salt.lt(limit)) {
    const saltString = salt
        .toHexString()
        .slice(2)
        .padStart(64, "0");

    salt = salt.add(1);

    const address = ethers.utils.getCreate2Address(from, Buffer.from(saltString, "hex"), initCodeHash).toLowerCase();

    if (address.indexOf("badc0de") !== -1) {
        console.log("address:", address);
        console.log("salt:", saltString);
        break;
    }
}
